mapper.update:
update Board
set bNick=#{bNick}, bPassword=#{bPassword}, bTitle=#{bTitle}, bDay=to_char(SYSDATE,'YYYY/MM/DD'),bTime=to_char(SYSDATE,'hh:mi:ss'),bImage=#{bImage}
where bNo = #{bNo}

update BReply
set rNick=#{rNick}, rPassword=#{rPassword}, rContent=#{rContent}, bTitle=#{bTitle}, rDay=to_char(SYSDATE,'YYYY/MM/DD'),rTime=to_char(SYSDATE,'hh:mi:ss')
where bNo = #{bNo}

mapper.select:
select count(*)
from vote
where bNo = #{bNo} AND mId = #{mId}

mapper.update:
update Board
set bVote = bVote+1
where bNo = #{bNo}

update Board
set bVote = bVote-1
where bNo = #{bNo}

update Board
set bViews = bViews + 1
where bNo = #{bNo}

mapper.select:
select count(*)
from Board
where mId = #{mId}
